{
    "commandLine": [
        "$(engine.path)\\accoreconsole.exe /i $(args[HostDwg].path) /s $(settings[script].path)"
    ],
    "parameters": {
        "HostDwg": {
            "verb": "get",
            "description": "Host drawing to be loaded into acad.",
            "localName": "$(HostDwg)"
        },
        "Result": {
            "verb": "post",
            "description": "Results",
            "localName": "result.pdf"
        },
        "Status": {
            "verb": "post",
            "description": "Results",
            "localName": "status.log"
        }
    },
    "engine": "Autodesk.AutoCAD+23",
    "appbundles": [],
    "settings": {
        "script": {
            "value": "_tilemode 0 -export _pdf _all result.pdf\n"
        }
    },
    "description": "PlotToPdf for all layouts.",
    "version": 1,
    "id": "myplot2pdf"
}

